all.map:
function (doc) {
  var PATHSEP = "/";
  var isActivation = function (doc) { return (doc.activationId !== undefined) };
  var collection = function (doc) {
    if (isActivation(doc)) return "activations";
    return "??";
  };

  if (isActivation(doc)) try {
    var ns = doc.namespace.split(PATHSEP);
    var root = ns[0]; ns.shift();
    var type = collection(doc);
    var date = new Date(doc.start || doc.updated);
    var value = {collection: type, namespace: doc.namespace, name: doc.name, version: doc.version, publish: doc.publish, annotations: doc.annotations};
    value.activationId = doc.activationId;
    emit([root, date], value);
  } catch (e) {}
}

activations.map:
function (doc) {
  var PATHSEP = "/";
  var isActivation = function (doc) { return (doc.activationId !== undefined) };

  if (isActivation(doc)) try {
    var date = new Date(doc.start || doc.updated);
    emit([doc.namespace, date.getTime(), doc.name], {namespace: doc.namespace, name: doc.name, version: doc.version, publish: doc.publish, annotations: doc.annotations, activationId: doc.activationId});
    emit([doc.namespace+PATHSEP+doc.name, date.getTime()], {namespace: doc.namespace, name: doc.name, version: doc.version, publish: doc.publish, annotations: doc.annotations, activationId: doc.activationId});
  } catch (e) {}
}
